import React, {useState} from "react";

//create variables in React with capital letter
const CountButton = () => {
    let currentCount = 0

    const handleClick = () => {
      currentCount++
    }
    return (
      <div>
        <button onClick={handleClick}>+1</button>
        <div>{currentCount}</div>
    </div>)
}

export default CountButton
